
public class PyramidSort {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int i=0; i<5; i++)
		
		{
			
			int k=1;
			for (int j=0; j<=i; j++)
			{
		
				System.out.print(k);
				System.out.print("\t");
				k++;
			}
			System.out.println("");
		}

	}

}
